import React from 'react';

const style = `
.btn-portfolio {
  height: 50px;
  min-width: 50px;
  width: 150px;
  border: 1px solid white;
  border-radius: 30px;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  fill: white;
  background-color: rgba(0,0,0,0.6);
}
.btn-portfolio:hover {
  background-color: rgba(256,256,256,0.6);
  color: black;
  fill: black;
}

.svg {
  height: 35px;
}

.btn-name {
  margin-left: 10px;
}
`;

const Buttons = ({ info }) => {
  return (
    <div className="btn-portfolio">
      <svg className="svg hover" viewBox="0 0 24 24" aria-hidden="true"><path d={info.logoPath} /></svg>
      <h4 className="btn-name hover">{info.name}</h4>
      <style>{style}</style>
    </div>
  );
};

export default Buttons;
