import React from 'react';

const style = `
.btn-portfolio {
  height: 50px;
  min-width: 50px;
  width: 150px;
  border: 1px solid white;
  border-radius: 30px;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  &:hover {
    background-color: white
  }
}

.svg {
  height: 35px;
}

.btn-name {
  margin-left: 10px;
}
`;

const Buttons = ({ info }) => {
  return (
    <div className="btn-portfolio">
      <svg className="svg" viewBox="0 0 24 24" aria-hidden="true" fill="white"><path d={info.logoPath} /></svg>
      <h4 className="btn-name">{info.name}</h4>
      <style>{style}</style>
    </div>
  );
};

export default Buttons;
